import React, { useEffect, useState, useRef } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import axios from "axios";

// Ïª®ÌÖåÏù¥ÎÑà Ïä§ÌÉÄÏùº
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  height: 100%;
  background-color: black;
  padding: 0;
  box-sizing: border-box;
`;

// Í≤ÄÏÉâÎ∞î Ïª®ÌÖåÏù¥ÎÑà Ïä§ÌÉÄÏùº
const SearchContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  max-width: 1200px;
  margin-top: 20px;
  margin-bottom: 40px;
`;

// Í≤ÄÏÉâ ÏûÖÎ†• Ïä§ÌÉÄÏùº
const SearchInput = styled.input`
  flex: 1;
  height: 30px;
  border: 1px solid white;
  background-color: black;
  color: white;
  outline: none;
  font-size: 16px;
  margin-right: 10px;
  padding-left: 10px;
`;

// Í≤ÄÏÉâ Î≤ÑÌäº Ïä§ÌÉÄÏùº
const SearchButton = styled.button`
  width: 70px;
  height: 30px;
  border: 1px solid white;
  background-color: white;
  color: black;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s;
`;

// Í∞ïÏùò Î¶¨Ïä§Ìä∏ Ïä§ÌÉÄÏùº
const LectureList = styled.div`
  width: 100%;
  max-width: 1200px;
  display: flex;
  overflow-x: auto;
  padding: 20px 0;
  gap: 20px;
  margin: 0 auto;
  border-radius: 15px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);

  &::-webkit-scrollbar {
    height: 8px;
    border-radius: 10px;
    border: 1px solid #fff;
  }

  &::-webkit-scrollbar-thumb {
    background: #561689;
    border-radius: 10px;
    border: 1px solid #fff;
  }
`;

// Í∞ïÏùò Ìï≠Î™© Ïä§ÌÉÄÏùº
const LectureItem = styled.div`
  position: relative;
  background-size: cover;
  background-position: center;
  background-image: url(${(props) => props.imageUrl});
  color: black;
  padding: 20px;
  font-size: 18px;
  cursor: pointer;
  width: 400px;
  height: 600px;
  box-sizing: border-box;
  transition: transform 0.2s ease, background-color 0.2s ease;
  scroll-snap-align: center;
  flex-shrink: 0;
  margin-right: 20px;
  overflow: hidden;

  &:hover {
    transform: scale(1.05); /* ÏÇ¥Ïßù ÌôïÎåÄ */
    z-index: 1;
  }

  &:hover::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5); /* Î∞∞Í≤Ω Ïñ¥ÎëêÏõåÏßê */
    z-index: 0;
  }
`;

const LectureTitle = styled.span`
  font-weight: bold;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  position: relative;
  z-index: 1;
  color: white; /* ÌÖçÏä§Ìä∏Í∞Ä Î∞∞Í≤Ω ÏúÑÏóê Ïûò Î≥¥Ïù¥ÎèÑÎ°ù Ìù∞ÏÉâÏúºÎ°ú ÏÑ§Ï†ï */
`;

const InfoText = styled.div`
  font-size: 16px;
  color: white;
  margin-top: 10px;
  position: relative;
  z-index: 1;
  transform: translateX(-100%); /* Ï≤òÏùåÏóî ÌôîÎ©¥ Î∞ñÏóê ÏúÑÏπò */
  transition: transform 0.5s ease, opacity 0.5s ease; /* Ïä¨ÎùºÏù¥Îìú Ïï†ÎãàÎ©îÏù¥ÏÖò */
  opacity: 0; /* Ï≤òÏùåÏóî Î≥¥Ïù¥ÏßÄ ÏïäÎèÑÎ°ù ÏÑ§Ï†ï */

  ${LectureItem}:hover & {
    transform: translateX(0); /* ÎßàÏö∞Ïä§ Ïò§Î≤Ñ Ïãú ÏôºÏ™ΩÏóêÏÑú Ïò§Î•∏Ï™ΩÏúºÎ°ú Ïä¨ÎùºÏù¥Îìú */
    opacity: 1; /* ÎßàÏö∞Ïä§ Ïò§Î≤Ñ Ïãú ÌÖçÏä§Ìä∏ Î≥¥Ïù¥ÎèÑÎ°ù ÏÑ§Ï†ï */
  }
`;

const Logo = styled.div`
  width: 100px;
  height: 100px;
  margin: 50px 0;
`;

export function LectureSearch() {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [lectures, setLectures] = useState([]);
  const searchRef = useRef(null);

  // Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Îäî Ìï®Ïàò
  const fetchLectures = async () => {
    try {
      const response = await axios.get("/api/findAllLecture");
      setLectures(response.data);
    } catch (error) {
      console.error("Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•ò: ", error);
    }
  };

  // Í≤ÄÏÉâ Ìï∏Îì§Îü¨
  const handleSearch = async () => {
    if (searchQuery.trim() === "") {
      alert("Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî.");
      return;
    }

    try {
      const response = await axios.get(
        "http://localhost:8080/api/searchLecture",
        {
          params: { query: searchQuery },
        }
      );

      if (response.data.length > 0) {
        setLectures(response.data);
      } else {
        alert("Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.");
        fetchLectures(); // Í∏∞Î≥∏ Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
      }
    } catch (error) {
      console.error("Í≤ÄÏÉâ Ï§ë Ïò§Î•ò Î∞úÏÉù: ", error);
      alert("Í≤ÄÏÉâ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      fetchLectures();
    }
  };

  // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä Ï≤òÏùå Î†åÎçîÎßÅÎê† Îïå Í∏∞Î≥∏ Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    fetchLectures();
  }, []);

  // Í∞ïÏùò ÌÅ¥Î¶≠ Ïãú Ïù¥Îèô
  const handleLectureClick = (id) => {
    navigate(`/lecture/${id}`);
  };

  // ÏóîÌÑ∞ ÌÇ§ ÏûÖÎ†• Í∞êÏßÄ Ìï∏Îì§Îü¨
  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  // Î°úÍ≥† ÌÅ¥Î¶≠ Ïãú Ï¥àÍ∏∞Ìôî Î∞è Ïä§ÌÅ¨Î°§ Ïù¥Îèô
  const handleLogoClick = () => {
    fetchLectures(); // Í∏∞Î≥∏ Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
    setSearchQuery(""); // Í≤ÄÏÉâÏñ¥ Ï¥àÍ∏∞Ìôî
    if (searchRef.current) {
      searchRef.current.scrollIntoView({ behavior: "smooth" }); // Í≤ÄÏÉâÏ∞ΩÏúºÎ°ú Ïä§ÌÅ¨Î°§ Ïù¥Îèô
    }
  };

  return (
    <Container>
      <SearchContainer ref={searchRef}>
        <SearchInput
          type="text"
          placeholder="LECTURE SEARCH"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <SearchButton onClick={handleSearch}>üîó</SearchButton>
      </SearchContainer>
      <LectureList>
        {lectures.map((lecture) => (
          <LectureItem
            key={lecture.id}
            imageUrl={lecture.imageUrl} /* Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ÏóêÏÑú Í∞ÄÏ†∏Ïò® Ïù¥ÎØ∏ÏßÄ URL */
            onClick={() => handleLectureClick(lecture.id)}
          >
            <LectureTitle>{lecture.lectureName}</LectureTitle>
            <InfoText>{lecture.description}</InfoText>
          </LectureItem>
        ))}
      </LectureList>
      <Logo>
        <a href="#" onClick={handleLogoClick}>
          <img
            src="./images/logo/logo_white.png"
            width="100"
            height="100"
            alt="untitled_logo"
          />
        </a>
      </Logo>
    </Container>
  );
}
