import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom"; 
import axios from "axios"; 

// ìŠ¤íƒ€ì¼ ì»´í¬ë„ŒíŠ¸
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  width: 100%;
  height: 100vh;
  background-color: black;
  padding: 30px; /* íŒ¨ë”©ì„ ì•½ê°„ í™•ëŒ€ */
  box-sizing: border-box;
`;

const TitleBar = styled.div`
  width: 100%;
  max-width: 1300px; /* ìµœëŒ€ ë„ˆë¹„ ì¦ê°€ */
  text-align: left;
  margin-bottom: 30px; /* í•˜ë‹¨ ì—¬ë°± ì¦ê°€ */
`;

const VideoTitle = styled.h1`
  color: white;
  font-weight: 300;
  font-size: 40px; /* ê¸€ìž í¬ê¸° ì¦ê°€ */
  margin: 0;
`;

const ContentArea = styled.div`
  display: flex;
  width: 100%;
  max-width: 1300px; /* ìµœëŒ€ ë„ˆë¹„ ì¦ê°€ */
  justify-content: space-between; /* ìš”ì†Œë“¤ì„ ì–‘ìª½ ëì— ì •ë ¬ */
  align-items: flex-start; /* ìƒë‹¨ ì •ë ¬ */
  gap: 30px; /* ìš”ì†Œ ê°„ì˜ ê°„ê²© ì¦ê°€ */
`;

const VideoPlayer = styled.div`
  flex: 4;
  width: 1920px; /* ë¹„ë””ì˜¤ í”Œë ˆì´ì–´ì˜ ê³ ì • ë„ˆë¹„ ì¦ê°€ */
  height: 1080px; /* ë¹„ë””ì˜¤ í”Œë ˆì´ì–´ì˜ ê³ ì • ë†’ì´ ì¦ê°€ */
  background-color: #444;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  /* margin-right: 20px;  */
`;

const ChatSection = styled.div`
  flex: 2;
  width: 400px; /* ì±„íŒ… ì„¹ì…˜ì˜ ê³ ì • ë„ˆë¹„ ì¦ê°€ */
  height: 600px; /* VideoPlayerì™€ ë™ì¼í•œ ë†’ì´ë¡œ ì„¤ì • */
  background-color: white;
  padding: 20px;
  box-sizing: border-box;
  overflow-y: auto;
`;

export function LectureDetail() {
  const { id } = useParams(); // URLì—ì„œ ê°•ì˜ IDë¥¼ ê°€ì ¸ì˜´
  const [videoData, setVideoData] = useState(null); // ë¹„ë””ì˜¤ ë°ì´í„°ë¥¼ ì €ìž¥í•  ìƒíƒœ
  const [chatMessages, setChatMessages] = useState([]); // ì±„íŒ… ë©”ì‹œì§€ë¥¼ ì €ìž¥í•  ìƒíƒœ

  useEffect(() => {
    // ë°±ì—”ë“œì—ì„œ ë¹„ë””ì˜¤ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
    axios.get(`/api/lectures/${id}`)
      .then(response => {
        setVideoData(response.data.videoUrl); // ë¹„ë””ì˜¤ URL ì„¤ì •
        setChatMessages(response.data.chatMessages); // ì´ˆê¸° ì±„íŒ… ë©”ì‹œì§€ ì„¤ì •
      })
      .catch(error => {
        console.error("Error fetching lecture data:", error);
      });
  }, [id]);

  return (
    <Container>
      <TitleBar>
        <VideoTitle>Lecture {id} ðŸ‘€</VideoTitle>
      </TitleBar>
      <ContentArea>
        <VideoPlayer>
          {videoData ? (
            <video width="100%" height="100%" controls>
              <source src={videoData} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          ) : (
            <p>Loading Video...</p>
          )}
        </VideoPlayer>
        <ChatSection>
          {/* ì‹¤ì‹œê°„ ì±„íŒ… ê³µê°„ */}
          {chatMessages.length > 0 ? (
            chatMessages.map((message, index) => (
              <p key={index}>{message}</p>
            ))
          ) : (
            <p>Loading Chat...</p>
          )}
        </ChatSection>
      </ContentArea>
    </Container>
  );
}
