import React, { useState } from "react";
import styled from "styled-components";

// ì»¨í…Œì´ë„ˆ ìŠ¤íƒ€ì¼
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  height: 100vh;
  background-color: black;
  padding: 40px;
`;

// ê²€ìƒ‰ë°” ì»¨í…Œì´ë„ˆ ìŠ¤íƒ€ì¼
const SearchContainer = styled.div`
  display: flex;
  align-items: center; 
  width: 500px;
  margin-bottom: 15px;
`;

// ê²€ìƒ‰ ìž…ë ¥ ìŠ¤íƒ€ì¼
const SearchInput = styled.input`

  flex: 1;
  height: 30px;
  border: 1px solid white;
  background-color: black;
  color: white;
  outline: none;
  font-size: 16px;
  margin-right: 10px;
  padding-left: 10px;
`;

// ê²€ìƒ‰ ë²„íŠ¼ ìŠ¤íƒ€ì¼
const SearchButton = styled.button`
  width: 70px; 
  height: 30px; 
  border: 1px solid white;
  background-color: white;
  color: black;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s;

`;

// ê°•ì˜ ë¦¬ìŠ¤íŠ¸ ìŠ¤íƒ€ì¼
const LectureList = styled.div`
  width: 500px;

`;

// ê°•ì˜ í•­ëª© ìŠ¤íƒ€ì¼
const LectureItem = styled.div`
  background-color: #fff;
  color: black;
  padding: 30px;
  padding-left: 20px;
  margin-bottom: 10px;
  border-radius: 15px;
  font-size: 25px;
  cursor: pointer; /* í´ë¦­ ê°€ëŠ¥í•œ í•­ëª©ìœ¼ë¡œ ì„¤ì • */
  transition: transform 0.2s ease, background-color 0.2s ease; /* ë¶€ë“œëŸ¬ìš´ ì „í™˜ íš¨ê³¼ */
  &:hover {
    background-color: #ccc; /* í˜¸ë²„ ì‹œ ì•½ê°„ì˜ ìƒ‰ìƒ ë³€í™” */
    transform: scale(1.05); /* í˜¸ë²„ ì‹œ ì•½ê°„ í™•ëŒ€ */
  }
`;

const LectureTitle = styled.span`
  font-weight: bold;
  font-weight: 500;

`

const InfoText = styled.div`
  font-size: 18px;
  color: black;
  margin-top: 5px;
`

export function LectureSearch() {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [lectures, setLectures] = useState(["Lecture 1", "Lecture 2", "Lecture 3", "Lecture 4"]);

  const handleSearch = () => {
    // ê²€ìƒ‰ ë¡œì§ ì¶”ê°€
    alert(`Searching for: ${searchQuery}`);
  };

  const goToLectureDetail = (lecture) => {
    const lectureId = lecture.split(" ")[1];
    navigate(`/lecture/${lectureId}`);
  }

  return (
    <>
      <Container>
        <SearchContainer>
          <SearchInput
            type="text"
            placeholder="LECTURE SEARCH"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <SearchButton onClick={handleSearch}>ðŸ”—</SearchButton>
        </SearchContainer>
        <LectureList>
          {lectures.map((lecture, index) => (
             <LectureItem key={index} onClick={() => goToLectureDetail(lecture)}>
              <LectureTitle>{lecture}</LectureTitle>
              <InfoText>info</InfoText>
            </LectureItem>
          ))}
        </LectureList>
      </Container>
    </>
  );
}
