import React from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom"; 

// Ïä§ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏
const Container = styled.div`
  display: flex;
  width: 100%;
  height: 100vh;
  background-color: black;
  padding: 20px;
`;

const VideoSection = styled.div`
  flex: 2;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: #333;
  margin-right: 20px;
  padding: 20px;
  border-radius: 10px;
`;

const VideoTitle = styled.h1`
  color: white;
  font-weight: 100;
  margin-bottom: 20px;
`;

const VideoPlayer = styled.div`
  width: 100%;
  max-width: 800px;
  aspect-ratio: 16/9;
  background-color: #444;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
`;

const ChatSection = styled.div`
  flex: 1;
  background-color: white;
  padding: 20px;
  border-radius: 10px;
  overflow-y: auto;
`;

// Í∏∞Î≥∏ Ïª¥Ìè¨ÎÑåÌä∏
export function LectureDetail() {
  const { id } = useParams(); 

  return (
    <Container>
      <VideoSection>
        <VideoTitle>Lecture {id} üëÄ</VideoTitle>
        <VideoPlayer>
          {/* Ïã§Ï†ú ÎπÑÎîîÏò§Î•º Ïó∞ÎèôÌï† Í≤ΩÏö∞ video ÌÉúÍ∑∏ ÎòêÎäî iframeÏúºÎ°ú Î≥ÄÍ≤Ω */}
          <button>Play Video</button>
        </VideoPlayer>
      </VideoSection>
      <ChatSection>
        {/* Ïã§ÏãúÍ∞Ñ Ï±ÑÌåÖ Íµ¨ÌòÑ ÏòàÏ†ï, API Ïó∞Îèô ÌõÑ ÏàòÏ†ï ÌïÑÏöî */}
        <div>Chat will be here.</div>
      </ChatSection>
    </Container>
  );
}
