import styled from "styled-components";
import { Navbar } from "./Navbar";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  background-color: black;
  padding-bottom: 5%;
  h3 {
    font-size: 60px;
    color: white;
    display: flex;
    align-items: center;
  }
`;
const Logo = styled.div`
  width: 200px;
  height: 200px;
  animation: rotate_image 10s linear infinite;
  transform-origin: 50% 50%;
  @keyframes rotate_image {
    100% {
      transform: rotate(360deg);
    }
  }
`;

const ThinText = styled.span`
  font-weight: 100; /* 얇은(Thin) 폰트 두께 설정 */
`;

const Rectangle = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 200px 0;
  border-width: 1px;
  border-color: white;
  border-style: solid;
  width: 90%;
  height: 100%;
`;

// 가로선을 위한 스타일 추가
const Line = styled.span`
  display: block;
  width: 100px; /* 선의 길이 조정 */
  height: 1px; /* 선의 두께 */
  background-color: white; /* 선의 색상 */
  margin-right: 10px; /* 텍스트와 선 사이의 간격 */
`;

export function Intro() {
  return (
    <>
      <Navbar />
      <Container>
        <Rectangle>
          <Logo>
            <a href="#">
              <img
                src="./images/logo/logo_white.png"
                width="200"
                height="200"
                alt="untitled_logo"
              />
            </a>
            <Line /> {/* 가로선 추가 */}
          </Logo>
          <h3>
            
            <ThinText>"Hello,</ThinText> <span>UNTITLED</span>
            <ThinText>"</ThinText>
          </h3>
        </Rectangle>
      </Container>
    </>
  );
}
