// LectureDetail.js
import React from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom"; // URLÏóêÏÑú Îß§Í∞úÎ≥ÄÏàòÎ•º Í∞ÄÏ†∏Ïò§Í∏∞ ÏúÑÌïú ÌõÖ

// Ïä§ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  width: 100%;
  height: 100vh;
  background-color: black;
  padding: 30px;
  box-sizing: border-box;
`;

const TitleBar = styled.div`
  width: 100%;
  max-width: 1200px; /* ÏµúÎåÄ ÎÑàÎπÑ ÏÑ§Ï†ï */
  text-align: left;
  margin-bottom: 20px;
`;

const VideoTitle = styled.h1`
  color: white;
  font-weight: 300;
  font-size: 36px;
  margin: 0;
`;

const ContentArea = styled.div`
  display: flex;
  width: 100%;
  max-width: 1200px; /* ÏµúÎåÄ ÎÑàÎπÑ ÏÑ§Ï†ï */
  justify-content: center; /* Ï§ëÏïô Ï†ïÎ†¨ */
  align-items: flex-start; /* ÏÉÅÎã® Ï†ïÎ†¨ */
  gap: 20px; /* ÏöîÏÜå Í∞ÑÏùò Í∞ÑÍ≤© */

  @media (min-width: 1200px) {
    /* ÌôîÎ©¥Ïù¥ Ï∂©Î∂ÑÌûà ÌÅ¥ Îïå */
    justify-content: space-between;
  }
`;

const VideoPlayer = styled.div`
  flex: 4;
  width: 800px; /* ÎπÑÎîîÏò§ ÌîåÎ†àÏù¥Ïñ¥Ïùò Í≥†Ï†ï ÎÑàÎπÑ */
  height: 550px; /* ÎπÑÎîîÏò§ ÌîåÎ†àÏù¥Ïñ¥Ïùò Í≥†Ï†ï ÎÜíÏù¥ */
  background-color: #444;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  border-radius: 10px;
  margin-right: 10px; /* Ïò§Î•∏Ï™Ω Í∞ÑÍ≤© */
`;

const ChatSection = styled.div`
  flex: 2;
  width: 350px; 
  height: 550px; 
  background-color: white;
  padding: 20px;
  border-radius: 10px;
  box-sizing: border-box;
  overflow-y: auto;
`;

export function LectureDetail() {
  const { id } = useParams(); // URLÏóêÏÑú Í∞ïÏùò IDÎ•º Í∞ÄÏ†∏Ïò¥

  return (
    <Container>
      <TitleBar>
        <VideoTitle>Lecture {id} üëÄ</VideoTitle>
      </TitleBar>
      <ContentArea>
        <VideoPlayer>
          <p>Play Video</p> {/* Ïã§Ï†ú ÎπÑÎîîÏò§Î•º ÏÇ¨Ïö©Ìï† Í≤ΩÏö∞ video ÌÉúÍ∑∏Î°ú ÎåÄÏ≤¥ */}
        </VideoPlayer>
        <ChatSection>
          {/* Ïã§ÏãúÍ∞Ñ Ï±ÑÌåÖ Í≥µÍ∞Ñ */}
          <p>Chat will be here.</p>
          <p>Message 1</p>
          <p>Message 2</p>
          <p>Message 3</p>
        </ChatSection>
      </ContentArea>
    </Container>
  );
}
