import React, { useState } from "react";
import styled from "styled-components";

// Ïª®ÌÖåÏù¥ÎÑà Ïä§ÌÉÄÏùº
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  height: 100vh;
  background-color: black;
  padding: 40px;
`;

// Í≤ÄÏÉâÎ∞î Ïª®ÌÖåÏù¥ÎÑà Ïä§ÌÉÄÏùº
const SearchContainer = styled.div`
  display: flex;
  align-items: center; 
  width: 500px;
  margin-bottom: 20px;
`;

// Í≤ÄÏÉâ ÏûÖÎ†• Ïä§ÌÉÄÏùº
const SearchInput = styled.input`
  flex: 1;
  height: 30px;
  border: 1px solid white;
  background-color: black;
  color: white;
  outline: none;
  font-size: 16px;
  margin-right: 5px;
  padding-left: 10px;
`;

// Í≤ÄÏÉâ Î≤ÑÌäº Ïä§ÌÉÄÏùº
const SearchButton = styled.button`
  width: 70px; 
  height: 30px; 
  border: 1px solid white;
  background-color: white;
  color: black;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s;

`;

// Í∞ïÏùò Î¶¨Ïä§Ìä∏ Ïä§ÌÉÄÏùº
const LectureList = styled.div`
  width: 500px;
  margin-top: 1px;
`;

// Í∞ïÏùò Ìï≠Î™© Ïä§ÌÉÄÏùº
const LectureItem = styled.div`
  background-color: #fff;
  color: black;
  padding: 30px;
  margin-bottom: 10px;
  border-radius: 15px; 
  font-size: 25px;
`;

const LectureTitle = styled.span`
  font-weight: bold;
`

export function LectureSearch() {
  const [searchQuery, setSearchQuery] = useState("");
  const [lectures, setLectures] = useState(["Lecture 1", "Lecture 2", "Lecture 3", "Lecture 4"]);

  const handleSearch = () => {
    // Í≤ÄÏÉâ Î°úÏßÅ Ï∂îÍ∞Ä
    alert(`Searching for: ${searchQuery}`);
  };

  return (
    <>
      <Container>
        <SearchContainer>
          <SearchInput
            type="text"
            placeholder="LECTURE SEARCH"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <SearchButton onClick={handleSearch}>üîó</SearchButton>
        </SearchContainer>
        <LectureList>
          {lectures.map((lecture, index) => (
            <LectureItem key={index}>
              <LectureTitle>{lecture}</LectureTitle>
              <div>info</div>
            </LectureItem>
          ))}
        </LectureList>
      </Container>
    </>
  );
}
