import React, { useEffect, useState, useRef } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import axios from "axios";

// Ïª®ÌÖåÏù¥ÎÑà Ïä§ÌÉÄÏùº
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  height: 100%;
  background-color: black;
  padding: 0;
  box-sizing: border-box;
`;

// Í≤ÄÏÉâÎ∞î Ïª®ÌÖåÏù¥ÎÑà Ïä§ÌÉÄÏùº (ÏõêÎûò ÏÉÅÌÉú Ïú†ÏßÄ)
const SearchContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  max-width: 1200px;
  margin-top: 20px;
  margin-bottom: 40px;
`;

// Í≤ÄÏÉâ ÏûÖÎ†• Ïä§ÌÉÄÏùº (ÏõêÎûò ÏÉÅÌÉú Ïú†ÏßÄ)
const SearchInput = styled.input`
  flex: 1;
  height: 30px;
  border: 1px solid white;
  background-color: black;
  color: white;
  outline: none;
  font-size: 16px;
  margin-right: 10px;
  padding-left: 10px;
`;

// Í≤ÄÏÉâ Î≤ÑÌäº Ïä§ÌÉÄÏùº (ÏõêÎûò ÏÉÅÌÉú Ïú†ÏßÄ)
const SearchButton = styled.button`
  width: 70px;
  height: 30px;
  border: 1px solid white;
  background-color: white;
  color: black;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s;
`;

// Í∞ïÏùò Î¶¨Ïä§Ìä∏ Ïä§ÌÉÄÏùº
const LectureList = styled.div`
  width: 100%;
  max-width: 1200px;
  display: flex;
  overflow-x: auto; /* Í∞ÄÎ°ú Ïä§ÌÅ¨Î°§ Í∞ÄÎä• */
  padding: 20px 0; /* ÏÉÅÌïò Ïó¨Î∞± Ï∂îÍ∞Ä */
  gap: 20px; /* Ìï≠Î™© Í∞ÑÏùò Í∞ÑÍ≤© ÏÑ§Ï†ï */
  margin: 0 auto; /* Í∞ÄÏö¥Îç∞ Ï†ïÎ†¨ÏùÑ ÏúÑÌï¥ ÎßàÏßÑ ÏÑ§Ï†ï */

  &::-webkit-scrollbar {
    height: 8px; /* Ïä§ÌÅ¨Î°§Î∞î ÎÜíÏù¥ ÏÑ§Ï†ï */
    background: #444; /* Ïä§ÌÅ¨Î°§Î∞î Î∞∞Í≤ΩÏÉâ */
    border-radius: 10px; /* Ïä§ÌÅ¨Î°§Î∞î Î™®ÏÑúÎ¶¨ Îë•Í∏ÄÍ≤å */
  }

  &::-webkit-scrollbar-thumb {
    background: #9900ff; /* Ïä§ÌÅ¨Î°§Î∞î ÏÜêÏû°Ïù¥ ÏÉâÏÉÅ */
    border-radius: 10px; /* Ïä§ÌÅ¨Î°§Î∞î ÏÜêÏû°Ïù¥ Î™®ÏÑúÎ¶¨ Îë•Í∏ÄÍ≤å */
    border: 2px solid #444; /* Ïä§ÌÅ¨Î°§Î∞î ÏÜêÏû°Ïù¥ÏôÄ Î∞∞Í≤Ω ÏÇ¨Ïù¥Ïùò Í≤ΩÍ≥Ñ */
  }
`;

// Í∞ïÏùò Ìï≠Î™© Ïä§ÌÉÄÏùº (Ï§ÑÎ¶¨ÏïÑ ÎßàÏö∞Ïä§ Ïò§Î≤Ñ Ìö®Í≥º Ï†ÅÏö©)
const LectureItem = styled.div`
  position: relative;
  background-color: #fff;
  color: black;
  padding: 0;
  border-radius: 15px;
  overflow: hidden;
  cursor: pointer;
  width: 400px; /* ÌôïÏû•Îêú ÎÑàÎπÑ */
  height: 600px; /* ÌôïÏû•Îêú ÎÜíÏù¥ */
  box-sizing: border-box;
  transition: transform 0.2s ease;
  scroll-snap-align: center; /* Ïä§ÌÅ¨Î°§ Ïä§ÎÉÖ Ìè¨Ïù∏Ìä∏ ÏÑ§Ï†ï */
  flex-shrink: 0; /* Ìï≠Î™©Ïù¥ Ï§ÑÏñ¥Îì§ÏßÄ ÏïäÎèÑÎ°ù ÏÑ§Ï†ï */
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;

  &:hover .info {
    transform: translateY(0);
  }
`;

const LectureImage = styled.div`
  width: 100%;
  height: 80%;
  background-size: cover;
  background-position: center;
  border-bottom: 2px solid #ddd;
`;

const LectureTitle = styled.h2`
  margin: 20px 0 10px;
  font-size: 24px;
  font-weight: bold;
`;

const InfoText = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background-color: rgba(0, 0, 0, 0.7);
  color: white;
  padding: 20px;
  transform: translateY(100%);
  transition: transform 0.4s ease;
`;

const Logo = styled.div`
  width: 100px;
  height: 100px;
  margin: 50px 0; /* Î°úÍ≥†ÏôÄ Í∞ÄÎ°ú Ïä§ÌÅ¨Î°§ ÏÇ¨Ïù¥Ïùò Í∞ÑÍ≤© Ï∂îÍ∞Ä */
`;

export function LectureSearch() {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [lectures, setLectures] = useState([]);
  const searchRef = useRef(null); // Í≤ÄÏÉâÏ∞ΩÏúºÎ°ú Ïä§ÌÅ¨Î°§ÌïòÍ∏∞ ÏúÑÌïú ref

  // Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Îäî Ìï®Ïàò
  const fetchLectures = async () => {
    try {
      const response = await axios.get("/api/findAllLecture");
      setLectures(response.data);
    } catch (error) {
      console.error("Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•ò: ", error);
    }
  };

  // Í≤ÄÏÉâ Ìï∏Îì§Îü¨
  const handleSearch = async () => {
    if (searchQuery.trim() === "") {
      alert("Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî.");
      return;
    }

    try {
      const response = await axios.get(
        "http://localhost:8080/api/searchLecture",
        {
          params: { query: searchQuery },
        }
      );

      if (response.data.length > 0) {
        setLectures(response.data);
      } else {
        alert("Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.");
        fetchLectures(); // Í∏∞Î≥∏ Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
      }
    } catch (error) {
      console.error("Í≤ÄÏÉâ Ï§ë Ïò§Î•ò Î∞úÏÉù: ", error);
      alert("Í≤ÄÏÉâ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      fetchLectures(); // Í∏∞Î≥∏ Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
    }
  };

  // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä Ï≤òÏùå Î†åÎçîÎßÅÎê† Îïå Í∏∞Î≥∏ Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    fetchLectures();
  }, []);

  // Í∞ïÏùò ÌÅ¥Î¶≠ Ïãú Ïù¥Îèô
  const handleLectureClick = (id) => {
    navigate(`/lecture/${id}`);
  };

  // ÏóîÌÑ∞ ÌÇ§ ÏûÖÎ†• Í∞êÏßÄ Ìï∏Îì§Îü¨
  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  // Î°úÍ≥† ÌÅ¥Î¶≠ Ïãú Ï¥àÍ∏∞Ìôî Î∞è Ïä§ÌÅ¨Î°§ Ïù¥Îèô
  const handleLogoClick = () => {
    fetchLectures(); // Í∏∞Î≥∏ Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
    setSearchQuery(""); // Í≤ÄÏÉâÏñ¥ Ï¥àÍ∏∞Ìôî
    if (searchRef.current) {
      searchRef.current.scrollIntoView({ behavior: "smooth" }); // Í≤ÄÏÉâÏ∞ΩÏúºÎ°ú Ïä§ÌÅ¨Î°§ Ïù¥Îèô
    }
  };

  return (
    <Container>
      <SearchContainer ref={searchRef}>
        <SearchInput
          type="text"
          placeholder="LECTURE SEARCH"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <SearchButton onClick={handleSearch}>üîó</SearchButton>
      </SearchContainer>
      <LectureList>
        {lectures.map((lecture) => (
          <LectureItem
            key={lecture.id}
            onClick={() => handleLectureClick(lecture.id)}
          >
            <LectureImage
              style={{ backgroundImage: `url(${lecture.imageUrl})` }}
            />
            <LectureTitle>{lecture.lectureName}</LectureTitle>
            <InfoText className="info">{lecture.description}</InfoText>
          </LectureItem>
        ))}
      </LectureList>
      <Logo>
        <a href="#" onClick={handleLogoClick}>
          <img
            src="./images/logo/logo_white.png"
            width="100"
            height="100"
            alt="untitled_logo"
          />
        </a>
      </Logo>
    </Container>
  );
}
