import React, { useEffect, useState, useRef } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import axios from "axios";

// 컨테이너 스타일
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  height: 100%;
  background-color: black;
  padding: 0;
  box-sizing: border-box;
`;

// 검색바 컨테이너 스타일 (원래 상태 유지)
const SearchContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  max-width: 1200px;
  margin-top: 20px;
  margin-bottom: 40px;
`;

// 검색 입력 스타일 (원래 상태 유지)
const SearchInput = styled.input`
  flex: 1;
  height: 30px;
  border: 1px solid white;
  background-color: black;
  color: white;
  outline: none;
  font-size: 16px;
  margin-right: 10px;
  padding-left: 10px;
`;

// 검색 버튼 스타일 (원래 상태 유지)
const SearchButton = styled.button`
  width: 70px;
  height: 30px;
  border: 1px solid white;
  background-color: white;
  color: black;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s;
`;

// 강의 리스트 스타일
const LectureList = styled.div`
  width: 100%;
  max-width: 1200px;
  display: flex;
  overflow-x: auto; /* 가로 스크롤 가능 */
  padding: 20px 0; /* 상하 여백 추가 */
  gap: 20px; /* 항목 간의 간격 설정 */
  margin: 0 auto; /* 가운데 정렬을 위해 마진 설정 */

  &::-webkit-scrollbar {
    height: 8px; /* 스크롤바 높이 설정 */
    background: #444; /* 스크롤바 배경색 */
    border-radius: 10px; /* 스크롤바 모서리 둥글게 */
  }

  &::-webkit-scrollbar-thumb {
    background: #9900ff; /* 스크롤바 손잡이 색상 */
    border-radius: 10px; /* 스크롤바 손잡이 모서리 둥글게 */
    border: 2px solid #444; /* 스크롤바 손잡이와 배경 사이의 경계 */
  }
`;

// 강의 항목 스타일 (줄리아 마우스 오버 효과 적용)
const LectureItem = styled.div`
  position: relative;
  width: 400px;
  height: 600px;
  background-color: #fff;
  overflow: hidden;
  cursor: pointer;
  border-radius: 15px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s ease;

  &:hover .info p {
    transform: translateX(0);
    opacity: 1;
  }
`;

const LectureImage = styled.div`
  width: 100%;
  height: 100%;
  background-size: cover;
  background-position: center;
  background-image: url(${(props) => props.imageUrl});
`;

const LectureTitle = styled.div`
  position: absolute;
  top: 20px;
  left: 20px;
  color: white;
  font-size: 24px;
  font-weight: bold;
  z-index: 2;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.6);
`;

const InfoText = styled.div`
  position: absolute;
  top: 50%;
  left: 20px;
  transform: translateY(-50%);
  color: white;
  z-index: 2;
  display: flex;
  flex-direction: column;
  gap: 10px;

  & p {
    background: rgba(255, 255, 255, 0.8);
    padding: 10px;
    transform: translateX(-100%);
    transition: transform 0.5s ease, opacity 0.5s ease;
    opacity: 0;
  }

  &:hover p {
    transform: translateX(0);
    opacity: 1;
  }
`;

const Logo = styled.div`
  width: 100px;
  height: 100px;
  margin: 50px 0; /* 로고와 가로 스크롤 사이의 간격 추가 */
`;

export function LectureSearch() {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [lectures, setLectures] = useState([]);
  const searchRef = useRef(null); // 검색창으로 스크롤하기 위한 ref

  // 강의 목록 불러오는 함수
  const fetchLectures = async () => {
    try {
      const response = await axios.get("/api/findAllLecture");
      setLectures(response.data);
    } catch (error) {
      console.error("강의 목록 불러오기 오류: ", error);
    }
  };

  // 검색 핸들러
  const handleSearch = async () => {
    if (searchQuery.trim() === "") {
      alert("검색어를 입력해 주세요.");
      return;
    }

    try {
      const response = await axios.get(
        "http://localhost:8080/api/searchLecture",
        {
          params: { query: searchQuery },
        }
      );

      if (response.data.length > 0) {
        setLectures(response.data);
      } else {
        alert("검색 결과가 없습니다.");
        fetchLectures(); // 기본 강의 목록 불러오기
      }
    } catch (error) {
      console.error("검색 중 오류 발생: ", error);
      alert("검색 중 오류가 발생했습니다.");
      fetchLectures(); // 기본 강의 목록 불러오기
    }
  };

  // 컴포넌트가 처음 렌더링될 때 기본 강의 목록 불러오기
  useEffect(() => {
    fetchLectures();
  }, []);

  // 강의 클릭 시 이동
  const handleLectureClick = (id) => {
    navigate(`/lecture/${id}`);
  };

  // 엔터 키 입력 감지 핸들러
  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  // 로고 클릭 시 초기화 및 스크롤 이동
  const handleLogoClick = () => {
    fetchLectures(); // 기본 강의 목록 불러오기
    setSearchQuery(""); // 검색어 초기화
    if (searchRef.current) {
      searchRef.current.scrollIntoView({ behavior: "smooth" }); // 검색창으로 스크롤 이동
    }
  };

  return (
    <Container>
      <SearchContainer ref={searchRef}>
        <SearchInput
          type="text"
          placeholder="LECTURE SEARCH"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <SearchButton onClick={handleSearch}>🔗</SearchButton>
      </SearchContainer>
      <LectureList>
        {lectures.map((lecture) => (
          <LectureItem
            key={lecture.id}
            onClick={() => handleLectureClick(lecture.id)}
          >
            <LectureImage imageUrl={lecture.imageUrl} />
            <LectureTitle>{lecture.lectureName}</LectureTitle>
            <InfoText className="info">
              {lecture.description.split("\n").map((line, index) => (
                <p key={index}>{line}</p>
              ))}
            </InfoText>
          </LectureItem>
        ))}
      </LectureList>
      <Logo>
        <a href="#" onClick={handleLogoClick}>
          <img
            src="./images/logo/logo_white.png"
            width="100"
            height="100"
            alt="untitled_logo"
          />
        </a>
      </Logo>
    </Container>
  );
}
