// LectureDetail.js
import React from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom"; // URL에서 매개변수를 가져오기 위한 훅

// 스타일 컴포넌트
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  width: 100%;
  height: 100vh;
  background-color: black;
  padding: 20px;
  box-sizing: border-box;

  @media (min-width: 1200px) {
    /* 화면이 충분히 클 때 가로로 배치 */
    flex-direction: row;
    justify-content: space-between;
  }
`;

const TitleBar = styled.div`
  display: flex;
  width: 100%;
  align-items: center;
  margin-bottom: 20px;
`;

const VideoTitle = styled.h1`
  color: white;
  font-weight: 300;
  font-size: 36px; // 크기를 적절히 조정
`;

const ContentArea = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  align-items: flex-start;

  @media (min-width: 1200px) {
    /* 화면이 충분히 클 때 가로로 배치 */
    flex-direction: row;
    justify-content: space-between;
  }
`;

const VideoPlayer = styled.div`
  width: 100%;
  max-width: 800px; // 최대 너비를 설정하여 반응형으로 동작
  height: 450px; // 비디오 플레이어 높이
  background-color: #444;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  border-radius: 10px;
  margin-bottom: 20px; // 비디오와 채팅 섹션 간의 간격

  @media (min-width: 1200px) {
    /* 화면이 충분히 클 때 */
    margin-bottom: 0;
    margin-right: 20px;
  }
`;

const ChatSection = styled.div`
  width: 100%;
  max-width: 300px; // 최대 너비를 설정하여 반응형으로 동작
  height: 450px; // VideoPlayer와 동일한 높이로 설정
  background-color: white;
  padding: 20px;
  border-radius: 10px;
  box-sizing: border-box;
  overflow-y: auto;
`;

export function LectureDetail() {
  const { id } = useParams(); // URL에서 강의 ID를 가져옴

  return (
    <Container>
      <TitleBar>
        <VideoTitle>Lecture {id} 👀</VideoTitle>
      </TitleBar>
      <ContentArea>
        <VideoPlayer>
          <p>Play Video</p> {/* 실제 비디오를 사용할 경우 video 태그로 대체 */}
        </VideoPlayer>
        <ChatSection>
          {/* 실시간 채팅 공간 */}
          <p>Chat will be here.</p>
          <p>Message 1</p>
          <p>Message 2</p>
          <p>Message 3</p>
        </ChatSection>
      </ContentArea>
    </Container>
  );
}
