import React, { useState } from "react";
import styled from "styled-components";


// 컨테이너 스타일
const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100vh;
  background-color: black;
  padding-bottom: 200px;
`;

// 얇은 텍스트 스타일
const Title = styled.h1`
  font-weight: 100;
  font-size: 80px;
  color: white;
  margin-bottom: 40px;
`;

// 폼 컨테이너 스타일
const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 500px;
`;

// 입력 필드 스타일
const Input = styled.input`
  width: 100%;
  padding: 10px;
  padding-left: 30px;
  margin: 10px 0;
  border: 1px solid white;
  border-radius: 30px;
  background-color: black;
  color: white;
  outline: none;
  font-size: 16px;
`;

// 달력 아이콘을 포함한 입력 필드 스타일
const DateInput = styled(Input)`
  background-image: url('calendar.png'); /* 달력 아이콘 이미지 경로 */
  background-size: 20px; /* 아이콘 크기 조정 */
  background-repeat: no-repeat;
  background-position: right 10px center;
`;

// 버튼 스타일
const Button = styled.button`
  width: 45%;
  padding: 10px;
  margin: 20px 0;
  border: 1px solid white;
  border-radius: 30px;
  background-color: white;
  color: black;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s; /* 부드러운 전환 효과 추가 */

  &:hover {
    background-color: #561689;
    color: white;
    border: none;
  }
`;

// 버튼 컨테이너 스타일
const ButtonContainer = styled.div`
  display: flex;
  justify-content: flex-end; /* 버튼을 오른쪽으로 정렬 */
  width: 100%;
`;

export function SignUp() {

  return (
    <>
      <Container>
        <Title>Sign Up</Title>
        <FormContainer>
          <Input type="text" placeholder="ID" />
          <Input type="password" placeholder="PW" />
          <Input type="text" placeholder="NAME" />
          <Input type="email" placeholder="EMAIL" />
          <DateInput Input type="date" placeholder="BIRTHDAY" />
          <ButtonContainer>
            <Button>Sign Up</Button>
          </ButtonContainer>
        </FormContainer>
      </Container>
    </>
  );
}
