import React, { useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom"; // React Router ÏÇ¨Ïö©

// Ïä§ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  height: 100vh;
  background-color: black;
  padding: 40px;
`;

// Í≤ÄÏÉâÎ∞î Ïª®ÌÖåÏù¥ÎÑà Ïä§ÌÉÄÏùº
const SearchContainer = styled.div`
  display: flex;
  align-items: center;
  width: 500px;
  margin-bottom: 15px;
`;

// Í≤ÄÏÉâ ÏûÖÎ†• Ïä§ÌÉÄÏùº
const SearchInput = styled.input`
  flex: 1;
  height: 30px;
  border: 1px solid white;
  background-color: black;
  color: white;
  outline: none;
  font-size: 16px;
  margin-right: 10px;
  padding-left: 10px;
`;

// Í≤ÄÏÉâ Î≤ÑÌäº Ïä§ÌÉÄÏùº
const SearchButton = styled.button`
  width: 70px;
  height: 30px;
  border: 1px solid white;
  background-color: white;
  color: black;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s;
`;

// Í∞ïÏùò Î¶¨Ïä§Ìä∏ Ïä§ÌÉÄÏùº
const LectureList = styled.div`
  width: 500px;
`;

// Í∞ïÏùò Ìï≠Î™© Ïä§ÌÉÄÏùº
const LectureItem = styled.div`
  background-color: #fff;
  color: black;
  padding: 30px;
  padding-left: 20px;
  margin-bottom: 10px;
  border-radius: 15px;
  font-size: 25px;
  cursor: pointer;
  transition: transform 0.2s ease, background-color 0.2s ease;
  &:hover {
    background-color: #ccc;
    transform: scale(1.05);
  }
`;

const LectureTitle = styled.span`
  font-weight: bold;
  font-weight: 500;
`;

const InfoText = styled.div`
  font-size: 18px;
  color: black;
  margin-top: 5px;
`;

export function LectureSearch() {
  const navigate = useNavigate();
  const [lectures] = useState([
    { id: 1, title: "Lecture 1", info: "Intro to Programming" },
    { id: 2, title: "Lecture 2", info: "Advanced JavaScript" },
    { id: 3, title: "Lecture 3", info: "React Basics" },
    { id: 4, title: "Lecture 4", info: "Node.js and Express" },
  ]);

  const handleLectureClick = (id) => {
    navigate(`/lecture/${id}`); // ÌÅ¥Î¶≠ Ïãú LectureDetail ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
  };

  return (
    <>
      <Container>
        <SearchContainer>
          <SearchInput
            type="text"
            placeholder="LECTURE SEARCH"
            onChange={(e) => console.log(e.target.value)}
          />
          <SearchButton>üîó</SearchButton>
        </SearchContainer>
        <LectureList>
          {lectures.map((lecture) => (
            <LectureItem key={lecture.id} onClick={() => handleLectureClick(lecture.id)}>
              <LectureTitle>{lecture.title}</LectureTitle>
              <InfoText>{lecture.info}</InfoText>
            </LectureItem>
          ))}
        </LectureList>
      </Container>
    </>
  );
}
