import React, { useEffect, useState, useRef } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import axios from "axios";

// 컨테이너 스타일
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  height: 100%;
  background-color: black;
  padding: 0;
  box-sizing: border-box;
`;

// 검색바 컨테이너 스타일
const SearchContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  max-width: 1200px;
  margin-top: 20px;
  margin-bottom: 40px;
`;

// 검색 입력 스타일
const SearchInput = styled.input`
  flex: 1;
  height: 30px;
  border: 1px solid white;
  background-color: black;
  color: white;
  outline: none;
  font-size: 16px;
  margin-right: 10px;
  padding-left: 10px;
`;

// 검색 버튼 스타일
const SearchButton = styled.button`
  width: 70px;
  height: 30px;
  border: 1px solid white;
  background-color: white;
  color: black;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s;
`;

// 강의 리스트 스타일 (가로 스크롤 설정 및 디자인 변경)
const LectureList = styled.div`
  width: 100%;
  max-width: 1200px;
  display: flex;
  overflow-x: auto; /* 가로 스크롤 가능 */
  padding-bottom: 20px; /* 스크롤바 여백 */
  padding-top: 20px; /* 상단 여백 추가 */
  scrollbar-width: thin; /* 스크롤바 너비 설정 (Firefox) */
  -ms-overflow-style: none; /* 스크롤바 숨김 (IE, Edge) */
  scroll-snap-type: x mandatory; /* 스크롤 스냅 설정 */
  gap: 20px; /* 항목 간의 간격을 동일하게 설정 */
  margin: 0 auto; /* 가운데 정렬을 위해 마진 설정 */

  &::-webkit-scrollbar {
    height: 5px; /* 제공된 코드와 일치하는 스크롤바 높이 설정 */
    background: #fff3; /* 스크롤바 배경색 */
    border-radius: 1ex; /* 스크롤바 모서리 둥글게 */
  }

  &::-webkit-scrollbar-thumb {
    background: var(--accent); /* 제공된 코드의 포커스 색상으로 설정 */
    border-radius: 1ex; /* 스크롤바 모서리 둥글게 */
  }

  &::-webkit-scrollbar-corner {
    background: #fff3; /* 스크롤바 코너 부분 배경색 */
  }
`;

// 강의 항목 스타일 (고정 크기 설정 및 여백 추가)
const LectureItem = styled.div`
  background-color: #fff;
  color: black;
  padding: 20px;
  font-size: 18px;
  cursor: pointer;
  width: 400px; /* 확장된 너비 */
  height: 600px; /* 확장된 높이 */
  box-sizing: border-box;
  transition: transform 0.2s ease, background-color 0.2s ease;
  scroll-snap-align: center; /* 스크롤 스냅 포인트 설정 */
  flex-shrink: 0; /* 항목이 줄어들지 않도록 설정 */
  margin-right: 20px; /* 항목 간 간격 동일하게 설정 */
  &:hover {
    background-color: #ccc;
    transform: scale(1.05);
    z-index: 1; /* 마우스 오버 시 잘리지 않도록 설정 */
  }
`;

const LectureTitle = styled.span`
  font-weight: bold;
  white-space: nowrap; /* 제목이 한 줄로 유지되도록 설정 */
  overflow: hidden; /* 넘치는 텍스트 숨김 */
  text-overflow: ellipsis; /* 넘치는 텍스트 생략 표시 (...) */
`;

const InfoText = styled.div`
  font-size: 16px;
  color: black;
  margin-top: 10px;
`;

const Logo = styled.div`
  width: 100px;
  height: 100px;
  margin: 50px 0; /* 로고와 가로 스크롤 사이의 간격 추가 */
`;

export function LectureSearch() {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [lectures, setLectures] = useState([]);
  const searchRef = useRef(null); // 검색창으로 스크롤하기 위한 ref

  // 강의 목록 불러오는 함수
  const fetchLectures = async () => {
    try {
      const response = await axios.get("/api/findAllLecture");
      setLectures(response.data);
    } catch (error) {
      console.error("강의 목록 불러오기 오류: ", error);
    }
  };

  // 검색 핸들러
  const handleSearch = async () => {
    if (searchQuery.trim() === "") {
      alert("검색어를 입력해 주세요.");
      return;
    }

    try {
      const response = await axios.get(
        "http://localhost:8080/api/searchLecture",
        {
          params: { query: searchQuery },
        }
      );

      if (response.data.length > 0) {
        setLectures(response.data);
      } else {
        alert("검색 결과가 없습니다.");
        fetchLectures(); // 기본 강의 목록 불러오기
      }
    } catch (error) {
      console.error("검색 중 오류 발생: ", error);
      alert("검색 중 오류가 발생했습니다.");
      fetchLectures(); // 기본 강의 목록 불러오기
    }
  };

  // 컴포넌트가 처음 렌더링될 때 기본 강의 목록 불러오기
  useEffect(() => {
    fetchLectures();
  }, []);

  // 강의 클릭 시 이동
  const handleLectureClick = (id) => {
    navigate(`/lecture/${id}`);
  };

  // 엔터 키 입력 감지 핸들러
  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  // 로고 클릭 시 초기화 및 스크롤 이동
  const handleLogoClick = () => {
    fetchLectures(); // 기본 강의 목록 불러오기
    setSearchQuery(""); // 검색어 초기화
    if (searchRef.current) {
      searchRef.current.scrollIntoView({ behavior: "smooth" }); // 검색창으로 스크롤 이동
    }
  };

  return (
    <Container>
      <SearchContainer ref={searchRef}>
        <SearchInput
          type="text"
          placeholder="LECTURE SEARCH"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <SearchButton onClick={handleSearch}>🔗</SearchButton>
      </SearchContainer>
      <LectureList>
        {lectures.map((lecture) => (
          <LectureItem
            key={lecture.id}
            onClick={() => handleLectureClick(lecture.id)}
          >
            <LectureTitle>{lecture.lectureName}</LectureTitle>
            <InfoText>{lecture.description}</InfoText>
          </LectureItem>
        ))}
      </LectureList>
      <Logo>
        <a href="#" onClick={handleLogoClick}>
          <img
            src="./images/logo/logo_white.png"
            width="100"
            height="100"
            alt="untitled_logo"
          />
        </a>
      </Logo>
    </Container>
  );
}
