// LectureDetail.js
import React, { useState } from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom"; // URLÏóêÏÑú Îß§Í∞úÎ≥ÄÏàòÎ•º Í∞ÄÏ†∏Ïò§Í∏∞ ÏúÑÌïú ÌõÖ

// Ïä§ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏
const Container = styled.div`
  display: flex;
  width: 100%;
  height: 100vh;
  background-color: black;
  padding: 20px;
`;

const VideoSection = styled.div`
  flex: 2;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: #333;
  margin-right: 20px;
  padding: 20px;
  border-radius: 10px;
`;

const VideoTitle = styled.h1`
  color: white;
  font-weight: 100;
  margin-bottom: 20px;
`;

const VideoPlayer = styled.div`
  width: 100%;
  max-width: 800px;
  height: 450px;
  background-color: #444;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  border-radius: 10px;
`;

const ChatSection = styled.div`
  flex: 1;
  background-color: white;
  padding: 20px;
  border-radius: 10px;
  overflow-y: auto;
  height: 100%;
`;

export function LectureDetail() {
  const { id } = useParams(); // URLÏóêÏÑú Í∞ïÏùò IDÎ•º Í∞ÄÏ†∏Ïò¥
  const [lectures] = useState([
    { id: 1, title: "Lecture 1", info: "Intro to Programming" },
    { id: 2, title: "Lecture 2", info: "Advanced JavaScript" },
    { id: 3, title: "Lecture 3", info: "React Basics" },
    { id: 4, title: "Lecture 4", info: "Node.js and Express" },
  ]);

  const lecture = lectures.find((lec) => lec.id === parseInt(id)); // ÏÑ†ÌÉùÌïú Í∞ïÏùòÎ•º Ï∞æÏùå

  if (!lecture) {
    return <Container><h1>Lecture not found</h1></Container>;
  }

  return (
    <Container>
      <VideoSection>
        <VideoTitle>{lecture.title} üëÄ</VideoTitle>
        <VideoPlayer>
          <p>Video Placeholder</p> {/* Ïã§Ï†ú ÎπÑÎîîÏò§Î•º ÏÇ¨Ïö©Ìï† Í≤ΩÏö∞ video ÌÉúÍ∑∏Î°ú ÎåÄÏ≤¥ */}
        </VideoPlayer>
      </VideoSection>
      <ChatSection>
        <p>Chat will be here.</p>
        <p>Message 1</p>
        <p>Message 2</p>
        <p>Message 3</p>
      </ChatSection>
    </Container>
  );
}
