import React from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom"; // URLÏóêÏÑú Îß§Í∞úÎ≥ÄÏàòÎ•º Í∞ÄÏ†∏Ïò§Í∏∞ ÏúÑÌïú ÌõÖ

// Ïä§ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏
const Container = styled.div`
  display: flex;
  flex-direction: row;
  width: 100%;
  height: 100vh;
  background-color: black;
  padding: 20px;
  box-sizing: border-box;
`;

const VideoTitle = styled.h1`
  color: white;
  font-weight: 300;
  margin-bottom: 20px;
  font-size: 36px; // ÌÅ¨Í∏∞Î•º Ï†ÅÏ†àÌûà Ï°∞Ï†ï
`;

const VideoPlayer = styled.div`
  width: 1100px; // Ï†ÅÏ†àÌïú ÎπÑÎîîÏò§ ÌîåÎ†àÏù¥Ïñ¥ ÎÑàÎπÑ
  height: 500px; // ÎÜíÏù¥ Ï°∞Ï†ï
  background-color: #444;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  margin-bottom: 20px; // ÎπÑÎîîÏò§ ÌÉÄÏù¥ÌãÄÍ≥ºÏùò Ïó¨Î∞±
`;

const ChatSection = styled.div`
  flex: 1;
  background-color: white;
  padding: 20px;
  border-radius: 10px;
  box-sizing: border-box;
  overflow-y: auto;
  height: 100%;
`;

export function LectureDetail() {
  const { id } = useParams(); // URLÏóêÏÑú Í∞ïÏùò IDÎ•º Í∞ÄÏ†∏Ïò¥

  return (
    <Container>
      <div>
        <VideoTitle>Lecture {id} üëÄ</VideoTitle>
        <VideoPlayer>
          <p>Play Video</p> {/* Ïã§Ï†ú ÎπÑÎîîÏò§Î•º ÏÇ¨Ïö©Ìï† Í≤ΩÏö∞ video ÌÉúÍ∑∏Î°ú ÎåÄÏ≤¥ */}
        </VideoPlayer>
      </div>
      <ChatSection>
        {/* Ïã§ÏãúÍ∞Ñ Ï±ÑÌåÖ Í≥µÍ∞Ñ */}
        <p>Chat will be here.</p>
        <p>Message 1</p>
        <p>Message 2</p>
        <p>Message 3</p>
      </ChatSection>
    </Container>
  );
}
