import React, { useState } from "react"; 
import styled from "styled-components";

const Container = styled.div`
  height: 100px;
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 0 auto;
  padding: 0 5%;
  background-color: black;
  color: white;
`;

const Menubar = styled.div`
  height: 50px;
  width: 50px;
`;

const Line = styled.span`
  display: block;
  width: 150px; /* 선의 길이 조정 */
  height: 1px; /* 선의 두께 */
  background-color: white; /* 선의 색상 */
  margin-right: 10px; /* 텍스트와 선 사이의 간격 */
`;

const LineContainer = styled.div`
  display: flex;
  align-items: center; /* 텍스트와 선의 수직 정렬을 맞추기 위해 추가 */
`;

const MenuContainer = styled.div`
  position: fixed;
  top: 0;
  right: ${({ isOpen }) => (isOpen ? "0" : "-50%")}; 
  width: 50%;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  backdrop-filter: blur(10px);
  transition: right 0.3s ease-in-out; 
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-end;
  padding-right: 30px;
  padding-bottom: 30px;
   z-index: 3;
`;

const MenuLink = styled.a`
  font-size: 7em;
  color: white;
  text-decoration: none;
  margin: 1px 0;
  font-weight: bold;
`;

const CloseButton = styled.div`
  position: absolute; 
  top: 20px; 
  right: 20px; 
  cursor: pointer; 
  padding-top: 10px;
`;

const LogoIcon = styled.div`
 position: absolute;
 bottom: 40px;
 right: 40px
`

export function Navbar() {
  const [isMenuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  return (
    <>
      <Container>
         <LineContainer>
           <Line/>
             <h3>UNTITLED</h3>
        </LineContainer>
        <Menubar onClick={toggleMenu}>
          <a href="#">
            <img
              src="./images/icon/menu.png"
              width="50"
              height="50"
              alt="menu"
            />
          </a>
        </Menubar>
      </Container>
      <MenuContainer isOpen={isMenuOpen}>
      <CloseButton onClick={toggleMenu}>
          <img
            src="./images/icon/close.png"
            width="50"
            height="50"
            alt="close"
          />
        </CloseButton>
      
          <MenuLink href="#intro">INTRO</MenuLink>
          <MenuLink href="#feed">FEED</MenuLink>
          <MenuLink href="#lecture">LECTURE</MenuLink>
          <MenuLink href="#mypage">MYPAGE</MenuLink>
        

        <LogoIcon>
          <img
            src="./images/logo/logo_white.png"
            width="70"
            height="70"
            alt="bottom icon"
          />
        </LogoIcon>
      </MenuContainer>
    </>
  );
}
