import React from 'react';
import styled, { keyframes } from 'styled-components';

// Simple shine animation using background
const shine = keyframes`
  0% {
    background-position: -200%;
  }
  100% {
    background-position: 200%;
  }
`;

const MenuContainer = styled.div`
  position: fixed;
  top: 0;
  right: ${({ isOpen }) => (isOpen ? "0" : "-50%")};
  width: 50%;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  backdrop-filter: blur(10px);
  transition: right 0.3s ease-in-out;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-end;
  padding-right: 30px;
  padding-bottom: 30px;
  z-index: 3;
`;

const MenuLink = styled.a`
  font-size: 6.5em;
  color: white;
  text-decoration: none;
  margin: 0px 0;
  font-weight: bold;
  position: relative;
  overflow: hidden;

  &:hover {
    background: linear-gradient(90deg, rgba(255,255,255,0.1), rgba(255,255,255,0.2), rgba(255,255,255,0.1));
    background-size: 200%;
    animation: ${shine} 1.5s linear infinite;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
  }
`;

const CloseButton = styled.div`
  position: absolute;
  top: 20px;
  right: 20px;
  cursor: pointer;
  padding-top: 25px;
`;

const LoginIcon = styled.a`
  position: absolute;
  top: 20px;
  right: 90px; 
  cursor: pointer;
  padding-top: 25px;
`;

const LogoIcon = styled.div`
  position: absolute;
  bottom: 40px;
  right: 40px;
`;

export function Sidebar({ isOpen, toggleMenu }) {
  return (
    <MenuContainer isOpen={isOpen}>
      <LoginIcon>
        <img
          src="./images/icon/login.png"
          width="50"
          height="50"
          alt="login"
        />
      </LoginIcon>

      <CloseButton onClick={toggleMenu}>
        <img
          src="./images/icon/close.png"
          width="50"
          height="50"
          alt="close"
        />
      </CloseButton>

      <MenuLink href="#">INTRO</MenuLink>
      <MenuLink href="#feed">FEED</MenuLink>
      <MenuLink href="#lecture">LECTURE</MenuLink>
      <MenuLink href="#mypage">MYPAGE</MenuLink>

      <LogoIcon>
        <img
          src="./images/logo/logo_white.png"
          width="70"
          height="70"
          alt="bottom icon"
        />
      </LogoIcon>
    </MenuContainer>
  );
}
