import styled from "styled-components";
import { Navbar } from "./Navbar";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  background-color: black;
  padding-bottom: 5%;
  h3 {
    font-size: 60px;
    color: white;
}
`;
const Logo = styled.div`
  width: 200px;
  height: 200px;
  animation: rotate_image 10s linear infinite;
  transform-origin: 50% 50%;
  @keyframes rotate_image {
    100% {
      transform: rotate(360deg);
    }
  }
`;

const ThinText = styled.span`
  font-weight: 100; /* 얇은(Thin) 폰트 두께 설정 */
`;

const Rectangle = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 200px 0;
  border-width: 1px;
  border-color: white;
  border-style: solid;
  width: 90%;
  height: 100%;
`;

export function Intro() {
  return (
    <>
      <Navbar />
      <Container>
        <Rectangle>
          <Logo>
            <a href="#">
              <img
                src="./images/logo/logo_white.png"
                width="200"
                height="200"
                alt="untitled_logo"
              />
            </a>
          </Logo>
          <h3>
            {/* <ThinText>"Hello,</ThinText> <span>UNTITLED</span>
            <ThinText>"</ThinText> */}

<h1>
  <a href="" class="typewrite" data-period="2000" data-type='[ "Hi, Im Si.", "I am Creative.", "I Love Design.", "I Love to Develop." ]'>
    <span class="wrap"></span>
  </a>
</h1>

          </h3>
        </Rectangle>
      </Container>
    </>
  );
}
