  overflow-y: auto;
`;

export function LectureDetail() {
  const { id } = useParams(); // URLì—ì„œ ê°•ì˜ IDë¥¼ ê°€ì ¸ì˜´

  return (
    <Container>
      <TitleBar>
        <VideoTitle>Lecture {id} ğŸ‘€</VideoTitle>
      </TitleBar>
