import React from 'react';
import styled from 'styled-components';

const MenuContainer = styled.div`
  position: fixed;
  top: 0;
  right: ${({ isOpen }) => (isOpen ? "0" : "-50%")};
  width: 50%;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  backdrop-filter: blur(10px);
  transition: right 0.3s ease-in-out;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-end;
  padding-right: 30px;
  padding-bottom: 30px;
  z-index: 3;
`;

const MenuLink = styled.a`
  font-size: 6.5em;
  color: white;
  text-decoration: none;
  margin: 0px 0;
  font-weight: bold;
`;

const ButtonContainer = styled.div`
  display: flex;
  align-items: center;
  position: absolute;
  top: 20px;
  right: 30px; /* 사이드바의 오른쪽에서 떨어져 있는 거리 */
`;

const CloseButton = styled.div`
  position: absolute;
  top: 20px;
  right: 20px;
  cursor: pointer;
  padding-top: 25px;
  margin-left: 20px;
`;

const LoginButton = styled.div`
  cursor: pointer;
  margin-right: 20px; /* 닫기 버튼과의 간격 조정 */
`;

const LogoIcon = styled.div`
  position: absolute;
  bottom: 40px;
  right: 40px;
`;

export function Sidebar({ isOpen, toggleMenu }) {
  return (
    <MenuContainer isOpen={isOpen}>
      <ButtonContainer>
        <LoginButton>
          <img
            src="./images/icon/login.png"
            width="50"
            height="50"
            alt="login"
          />
        </LoginButton>
        <CloseButton onClick={toggleMenu}>
          <img
            src="./images/icon/close.png"
            width="50"
            height="50"
            alt="close"
          />
        </CloseButton>
      </ButtonContainer>

      <MenuLink href="#">INTRO</MenuLink>
      <MenuLink href="#feed">FEED</MenuLink>
      <MenuLink href="#lecture">LECTURE</MenuLink>
      <MenuLink href="#mypage">MYPAGE</MenuLink>

      <LogoIcon>
        <img
          src="./images/logo/logo_white.png"
          width="70"
          height="70"
          alt="bottom icon"
        />
      </LogoIcon>
    </MenuContainer>
  );
}
