// LectureDetail.js
import React from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom"; // URLÏóêÏÑú Îß§Í∞úÎ≥ÄÏàòÎ•º Í∞ÄÏ†∏Ïò§Í∏∞ ÏúÑÌïú ÌõÖ

// Ïä§ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏
const Container = styled.div`
  display: flex;
  flex-direction: row;
  align-items: flex-start; // ÏÉÅÎã®Ïóê Ï†ïÎ†¨
  justify-content: space-between; // ÏñëÏ™Ω Ï†ïÎ†¨Î°ú Í≥µÍ∞Ñ Î∂ÑÎ∞∞
  width: 100%;
  height: 100vh;
  background-color: black;
  padding: 20px;
  box-sizing: border-box;
`;

const LeftSection = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  width: 65%; // ÌôîÎ©¥Ïùò 65% Ï∞®ÏßÄ
  padding-right: 20px; // Ïò§Î•∏Ï™Ω Ïó¨Î∞± Ï∂îÍ∞Ä
`;

const VideoTitle = styled.h1`
  color: white;
  font-weight: 300;
  margin-bottom: 20px;
  font-size: 36px; // ÌÅ¨Í∏∞Î•º Ï†ÅÏ†àÌûà Ï°∞Ï†ï
`;

const VideoPlayer = styled.div`
  width: 100%;
  height: 450px; // ÎπÑÎîîÏò§ ÌîåÎ†àÏù¥Ïñ¥ ÎÜíÏù¥ Ï°∞Ï†ï
  background-color: #444;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  border-radius: 10px;
`;

const ChatSection = styled.div`
  width: 30%; // ÌôîÎ©¥Ïùò 30% Ï∞®ÏßÄ
  height: 500px; // VideoPlayerÏôÄ ÎπÑÏä∑Ìïú ÎÜíÏù¥Î°ú ÏÑ§Ï†ï
  background-color: white;
  padding: 20px;
  border-radius: 10px;
  box-sizing: border-box;
  overflow-y: auto;
`;

export function LectureDetail() {
  const { id } = useParams(); // URLÏóêÏÑú Í∞ïÏùò IDÎ•º Í∞ÄÏ†∏Ïò¥

  return (
    <Container>
      <LeftSection>
        <VideoTitle>Lecture {id} üëÄ</VideoTitle>
        <VideoPlayer>
          <p>Play Video</p> {/* Ïã§Ï†ú ÎπÑÎîîÏò§Î•º ÏÇ¨Ïö©Ìï† Í≤ΩÏö∞ video ÌÉúÍ∑∏Î°ú ÎåÄÏ≤¥ */}
        </VideoPlayer>
      </LeftSection>
      <ChatSection>
        {/* Ïã§ÏãúÍ∞Ñ Ï±ÑÌåÖ Í≥µÍ∞Ñ */}
        <p>Chat will be here.</p>
        <p>Message 1</p>
        <p>Message 2</p>
        <p>Message 3</p>
      </ChatSection>
    </Container>
  );
}
